    private static int[] CreateArray(int length)
    {
        var random = new Random();
        var array = new int[length];
        var exists = new bool[length];

        for (int i = 0; i < length;)
        {
            var newInt = random.Next(length);
            if (exists[newInt] == false)
            {
                exists[newInt] = true;
                array[i++] = newInt + 1;
            }
        }

        return array;
    }

    public static void Boblesortering(int[] a)     // hører til klassen Tabell
    {
        for (int n = a.Length; n > 1; n--)           // n reduseres med 1 hver gang
        {
            for (int i = 1; i < n; i++)                // går fra 1 til n
            {
                if (a[i - 1] < a[i]) Table.Swap(a, i - 1, i);  // sammenligner/bytter
            }
        }
    }

