class Table
{
    public static int Min(int[] arr, int from, int to)
    {
        int minIndex = from;
        int minVal = arr[from];

        for (int i = from + 1; i < to; i++)
        {
            if (arr[i] < minVal)
            {
                minIndex = i;
                minVal = arr[minIndex];
            }
        }

        return minIndex;
    }

    public static int Min(int[] arr)
    {
        return Min(arr, 0, arr.Length);
    }


    public static int Max(int[] arr, int from, int to)
    {
        int maxIndex = from;
        int maxVal = arr[from];

        for (int i = from + 1; i < to; i++)
        {
            if (arr[i] > maxVal)
            {
                maxIndex = i;
                maxVal = arr[maxIndex];
            }
        }

        return maxIndex;
    }

    public static int Max(int[] arr)
    {
        return Max(arr, 0, arr.Length);
    }


    public static void Swap(int[] arr, int index1, int index2)
    {
        (arr[index1], arr[index2]) = (arr[index2], arr[index1]);
    }


    public static void Rotate(int[] arr)
    {
        int n = arr.Length - 1;

        for (int i = 0; i < arr.Length / 2; i++)
        {
            (arr[i], arr[n - i]) = (arr[n - i], arr[i]);
        }
    }


    public static int SelectionSort(int[] arr)
    {
        int count = default;

        for (int i = 0; i < arr.Length - 1; i++)
        {
            int index = i;
            int value = arr[i];

            for (int j = i + 1; j < arr.Length; j++)
            {
                if (arr[j] < value)
                {
                    index = j;
                    value = arr[index];
                }
            }

            if (index == i) count++;
            else (arr[i], arr[index]) = (arr[index], arr[i]);
        }

        return count;
    }

    public static void SelectionSort(int[] arr, int from, int to)
    {
        if (arr.Length < 0 || from >= to || to > arr.Length) throw new ArgumentException("Error, params are wrong!");

        for (int i = from; i < to - 1; i++)
        {
            int minIndex = i;
            int minVal = arr[i];

            for (int j = i + 1; j < to; j++)
            {
                if (arr[j] < minVal)
                {
                    minIndex = j;
                    minVal = arr[minIndex];
                }
            }

            (arr[i], arr[minIndex]) = (arr[minIndex], arr[i]);
        }
    }

    public static void SelectionSortAsc(int[] arr)
    {
        for (int i = 0; i < arr.Length - 1; i++)
        {
            int index = i;
            int value = arr[i];

            for (int j = i + 1; j < arr.Length; j++)
            {
                if (arr[j] > value)
                {
                    index = j;
                    value = arr[index];
                }
            }

            (arr[i], arr[index]) = (arr[index], arr[i]);
        }
    }

    public static void SelectionSortMax(int[] arr)
    {
        for (int i = arr.Length - 1; i >= 0; i--)
        {
            int index = i;
            int value = arr[i];

            for (int j = 0; j < i; j++)
            {
                if (arr[j] > value)
                {
                    index = j;
                    value = arr[index];
                }
            }

            (arr[i], arr[index]) = (arr[index], arr[i]);
        }
    }


    public static void SelectionSortHelper(int[] arr)
    {
        for (int i = 0; i < arr.Length - 1; i++)
        {
            Swap(arr, i, Min(arr, i, arr.Length));
        }
    }

    public static void SelectionSortHelperMax(int[] arr)
    {
        for (int i = arr.Length; i > 1; i--)
        {
            Swap(arr, Max(arr, 0, i), i - 1);
        }
    }

}